libs.content:
find_library(openssl_ssl_library
    names
      ssl
      ssleay32
      ssleay32md
    names_per_dir
    ${_openssl_root_hints_and_paths}
    hints
      ${_openssl_libdir}
      ${_openssl_library_dirs}
    path_suffixes
      lib lib64
  )

find_library(openssl_crypto_library
    names
      crypto
    names_per_dir
    ${_openssl_root_hints_and_paths}
    hints
      ${_openssl_libdir}
      ${_openssl_library_dirs}
    path_suffixes
      lib lib64
  )
